PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `Users` (`id` INTEGER PRIMARY KEY, `username` VARCHAR(255) NOT NULL UNIQUE, `email` VARCHAR(255) NOT NULL UNIQUE, `password` VARCHAR(255) NOT NULL, `role` TEXT DEFAULT 'reader', `bio` TEXT, `avatar` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Categories` (`id` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL UNIQUE, `slug` VARCHAR(255) NOT NULL UNIQUE, `description` TEXT, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Articles` (`id` INTEGER PRIMARY KEY, `title` VARCHAR(255) NOT NULL, `slug` VARCHAR(255) NOT NULL UNIQUE, `content` TEXT NOT NULL, `excerpt` TEXT, `coverImage` VARCHAR(255), `published` TINYINT(1) DEFAULT 0, `views` INTEGER DEFAULT '0', `authorId` INTEGER NOT NULL REFERENCES `Users` (`id`), `categoryId` INTEGER REFERENCES `Categories` (`id`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Comments` (`id` INTEGER PRIMARY KEY, `content` TEXT NOT NULL, `articleId` INTEGER NOT NULL REFERENCES `Articles` (`id`), `userId` INTEGER NOT NULL REFERENCES `Users` (`id`), `parentId` INTEGER REFERENCES `Comments` (`id`), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `Tags` (`id` INTEGER PRIMARY KEY, `name` VARCHAR(255) NOT NULL UNIQUE, `slug` VARCHAR(255) NOT NULL UNIQUE, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
CREATE TABLE `ArticleTags` (`createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `ArticleId` INTEGER NOT NULL UNIQUE REFERENCES `Articles` (`id`), `TagId` INTEGER NOT NULL UNIQUE REFERENCES `Tags` (`id`), PRIMARY KEY (`ArticleId`, `TagId`));
CREATE TABLE `Subscriptions` (`createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, `authorId` INTEGER NOT NULL UNIQUE REFERENCES `Users` (`id`), `subscriberId` INTEGER NOT NULL UNIQUE REFERENCES `Users` (`id`), PRIMARY KEY (`authorId`, `subscriberId`));
CREATE TABLE `Users_backup` (`id` INTEGER PRIMARY KEY, `username` VARCHAR(255) NOT NULL UNIQUE, `email` VARCHAR(255) NOT NULL UNIQUE, `password` VARCHAR(255) NOT NULL, `role` TEXT DEFAULT 'reader', `bio` TEXT, `avatar` VARCHAR(255), `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL);
DELETE FROM sqlite_sequence;
COMMIT;
